.clock-container {
            border-width: 1px;
            border-style: solid;
            border-radius: 3px;
            width: 100%;
            height: 72px;
            position: relative;
            text-align: center;
            font-weight: 300;
            overflow: hidden;
            box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.37);
        }

        .clock-container * {
            cursor: default;
        }

        .am-pm-container {
            position: absolute;
            width: 40px;
            right: 0;
            top: 0;
            line-height: 20px;
            font-weight: 400;
            font-size: 12px;
            height: 40px;
            padding-top: 5px;
        }

        .am-indicator,
        .am-mode .pm-indicator {
            opacity: 0.3;
        }

        .am-mode .am-indicator,
        .pm-indicator {
            opacity: 1.0;
        }

        .clock-time {
            line-height: 50px;
            height: 50px;
            width: 100%;
            border-width: 0;
            border-width-bottom: 1px;
            border-style: solid;
            font-size: 22px;
            position: relative;
            top: 0;
            left: 0;
            transition: top 0.4s;
        }

        @keyframes pulsate {
            0% {
                /*-webkit-transform: scale(0.1, 0.1);*/
                opacity: 0.5;
            }
            25% {
                opacity: 1.0;
            }
            75% {
                opacity: 1.0;
            }
            100% {
                /*-webkit-transform: scale(1.2, 1.2);*/
                opacity: 0.5;
            }
        }

        .clock-separator {
            animation: pulsate 1s infinite;
        }

        .clock-date {
            height: 29px;
            line-height: 30px;
            bottom: 0;
            left: 0;
            position: absolute;
            width: 100%;
            transition: bottom 0.4s;
            border-width: 0;
            border-top-width: 1px;
            border-style: solid;

        }

        .picker-mode .clock-time {
            top: -51px;
        }

        .picker-mode .clock-date {
            bottom: -30px;
        }

        .location-list-container {
            transform: scaleY(0.0);
            text-align: left;
            position: absolute;
            top: 0;
            left: 0;
            transition: transform 0.4s;
            padding-left: 10px;
            padding-top: 5px;
            font-size: 14px;
            line-height: 23px;
            font-weight: 500;
        }

        .picker-mode .location-list-container {
            transform: scaleY(1.0);
        }

        ul.location-list {
            list-style-type: none;
            padding: 0;
            margin: 0;
        }

        ul.location-list li {
            display: inline-block;
            width: 50%;
            cursor: pointer;
        }

        ul.location-list li#myTime {
            width: 80%;
        }

        ul.location-list li:active {
            scale: 0.9;
        }

        .time-mode-button {
            font-weight: bold;
            font-size: 12px;
            text-transform: uppercase;
            color: white;
            margin-bottom: 5px;
            cursor: pointer;
            transition: opacity 0.4s;
        }

        .picker-mode .time-mode-button {
            opacity: 0;
        }

