$(".time-mode-button").click(function() {
   $(".clock-parent-container").toggleClass("picker-mode");
});


require(['https://cdnjs.cloudflare.com/ajax/libs/moment-timezone/0.5.0/moment-timezone-with-data.min.js'], function (moment) {
        $("ul.location-list li").click(function() {
            var tz = $(this).attr('data-timezone');
            var title = $(this).text();
            window.ltClockTimeZone = tz;
            displayTime();
            $(".time-mode-button").text(title);
            $(".clock-parent-container").removeClass("picker-mode");
        });

            //runs functions to display time when active.
	function displayTime() {
        if (typeof window.ltClockTimeZone == "undefined" || window.ltClockTimeZone == "localtime") {
            getTime();
        } else {
            getTime(window.ltClockTimeZone);
        }
	}

        //gets time from timezone
	function getTime(timezone) {
		//Get date and time from moment.tz with correct formatting
		var clock = moment.tz();

        if (typeof timezone !== "undefined") {
            clock = moment.tz(timezone);
        }

		var hour = clock.format('hh');
        var min = clock.format('mm');
        var isAm = clock.format('a').indexOf('a');
        if (isAm != -1) {
            $(".clock-container").addClass('am-mode');
        } else {
            $(".clock-container").removeClass('am-mode');
        }

		var date = clock.format('dddd, MMM Do');

		//Add date and time to their divs
		$('.clock-hour-value').text(hour);
        $('.clock-min-value').text(min);
		$('.clock-date-value').text(date);
	}

	// Runs the displayTime function the first time
	displayTime();

	// Makes our clock 'tick' by repeatedly
	// running the displayTime function every second.
    setInterval(displayTime, 1000);

});
