defaultConfig.css:
.menuWrapper {
    height: 30px;
    padding-top: 25px;
    padding-bottom: 25px;
}
.menuIcon {
    display: inline-block;
    cursor: pointer;
}
.logo {
    display: inline-block;
    margin-left: 10px;
    width: 80px;
}
.bar1, .bar2, .bar3 {
    width: 30px;
    height: 2px;
    background-color: #E8E8E8;
    margin: 8px 0;
    transition: 0.4s;
}

.menuClose .bar1 {
    -webkit-transform: rotate(-45deg) translate(-10px, 5px) ;
    transform: rotate(-45deg) translate(-10px, 5px) ;
}

.menuClose .bar2 {opacity: 0;}

.menuClose .bar3 {
    -webkit-transform: rotate(45deg) translate(-9px, -5px) ;
    transform: rotate(45deg) translate(-9px, -5px) ;
}
