children.css:
#@{uniqueId} {
  height: 336px;
  overflow-y:scroll;
  background: #F9F9F9;
  border-bottom: 1px solid rgba(85, 85, 85, 0.1);
}

h3.sites_heading {
  font-size: 16px;  
  text-align: center;
  color: #fff;;
  background: rgb(61, 58, 59);
  padding:22px 0px;
  margin-top: 0px;
  display: none;

}

a.sites_container {
  padding: 10px;
  display: block;
  font-size: 16px;
  color: rgb(61, 58, 59);
}

a.sites_container:hover {
  text-decoration: none;
  background: rgba(0,0,0,0.2) !important;
}

a.sites_container:nth-child(odd) {
    background: rgba(0,0,0,0.07);
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
}

a.sites_container span {
    font-size: 20px;
    color: rgb(61, 58, 59);
    float: right;
    margin-right: 15px;
    font-weight: bold;
    line-height: 24px;
}

a.view_more_sites {
  margin: 10px;
  font-size: 14px;
  background: rgb(61, 58, 59);
  padding: 10px 20px;
  color: #fff;
}

.view_more_container {
  padding: 20px 0px;
  background: #F9F9F9;
  border-top: 1px solid rgba(0,0,0,0.05);
}

#@{uniqueId}::-webkit-scrollbar {
    width: 1em;
}

#@{uniqueId}::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
}

#@{uniqueId}::-webkit-scrollbar-thumb {
  background-color: darkgrey;
}

