defaultConfig.css:
#@{uniqueId} {
    font-family: Lato, 'Segoe UI', 'Helvetica Neue', sans-serif;
    color: @color;

    h1{
        margin-top: 0px;
        text-align: center;
    }

    .banner{
        background-color: @altColor;
        border-bottom: 1px solid #cecece;
        color:white;
    }

    .header{
        font-size: 20px;
        padding: 20px;
    } 

    .messagesContainer{
        font-size: 13px;

        h4{
            font-size: 18px;
        }

        .messages{
            border-bottom: 1px solid #cecece;
            padding-left: 10px;
        }
    }
}

defaultConfig.js:
var $list = $("#" + uniqueId + " .messagesContainer");

LiveTilesApi.Graph.get({ path: "/me/messages"}).then(function(result) {

    for(var i in result.data.value) {
        var msg = result.data.value[i];
        $list.append($(`
            <div class="messages">
                <h4>${msg.from.emailAddress.address}</h4>
                <p>${msg.bodyPreview}</p>
            </div>
        `));
    }
});
