children.css:
#content {
  padding: 0px;
  font-family: 'Raleway';
    color: #fff; // add fontColor
  font-size: 18px;
  margin: 5px 0px;
}

ul#favorite-links {
  padding: 0;
  margin: 0px;
}

ul#favorite-links li {
  margin: 5px 0px;
  list-style: none;
  background: @linkColor;
  padding: 6px;
  margin-right: 10px;
  height: 38px;
  display: inline-block;
  min-width: 11%;
}

#favorite-links a {
    color: #fff; // add fontColor
  text-decoration:none;
  font-size: 18px;
  margin-right: 10px;
  font-family: 'Raleway';
}

#favorite-links li {
  padding:10px 20px 10px 0px;
  display: inline;
}

#favorite-links li:last-child {
  border:none;
}

.removebtn { 
  border:none;
  background: none;
    color: #fff; // add fontColor
  padding: 0px;
  margin-top: -4px;
  font-size: 14px;
}

.removebtn:hover {
  background: rgba(0,0,0,0); 
  cursor: pointer;
}

/*ul#favorite-links li:nth-of-type(1n+6) {
  display: none;
}*/

#add-link-form {
  display:none;
  background: @formBackgroundColor;
  padding:20px;
    color: #fff; // add fontColor
  margin: 10px;
  width: 60%;
  height: 250px;
  font-size: 18px;
  font-family: 'Raleway'
}

#add-link-form input {
  margin-bottom: 10px;
  width: 80%;
  height: 40px;
  color: #333;
  padding:5px;
  outline:none;
  border:none;
  font-family: 'Raleway';
  font-size: 16px;
}

#new-link-button {
    background: @buttonColor;
    color: #fff; // add fontColor
    height: 38px;
    float: left;
    width: 50px;
    font-family: 'Raleway';
    text-align: center;
    font-size: 45px;
    line-height: 40px;
    cursor: pointer;
    margin: 5px 10px 5px 0px;
}

#add-link-form button {
    background: @buttonColor;
    border: none;
    color: #fff; // add fontColor
    padding: 5px 15px;
    height: 38px;
    float: left;
    width: 120px;
    margin-top: 15px;
    font-family: 'Raleway';
    font-size: 18px;
}

#max-links {
    background: #ccc;
    color: #fff;
    height: 38px;
    float: left;
    width: 50px;
    font-family: 'Raleway';
    font-weight: bold;
    text-align: center;
    font-size: 22px;
    line-height: 38px;
    margin: 5px 10px 5px 0px;
}

