$(document).ready(function() {
  var $ul = $('#favorite-links');
  var $title = $('#title');
  var $url = $('#url');

  //get items from local storage
  if(localStorage.getItem('vk-links')){
   $ul.html(localStorage.getItem('vk-links'));
  }

    // Check if items are >= 7
    if ($('ul#favorite-links > li').length >= 7) {
        $('#new-link-button').hide();
        $('#max-links').show();
    } else {
        $('#new-link-button').show();
        $('#max-links').hide();
    }

  // add new item
  $('#add').click(function (e) {
    e.preventDefault();  

    //add new item
    $('#favorite-links').append('<li><a target="_new" href="'+$url.val()+'">'+$title.val()+'</a><a class="removebtn">x</a></li>');

    // Check if items are >= 7
    if ($('ul#favorite-links > li').length >= 7) {
        $('#new-link-button').hide();
        $('#max-links').show();
    } else {
        $('#new-link-button').show();
        $('#max-links').hide();
    }

    //save changes to localstorage
    localStorage.setItem('vk-links', $ul.html());

    //reset form
    $title.val("");
    $url.val("");
    $("#add-link-form").slideToggle("100");

  });

  //remove item
  $("#favorite-links").on('click','.removebtn',function(e) {
    e.preventDefault();  
    $(this).parent().remove();
    //save changes to localstorage
    localStorage.setItem('vk-links', $ul.html());

    // Check if items are >= 7
    if ($('ul#favorite-links > li').length >= 7) {
        $('#new-link-button').hide();
        $('#max-links').show();
    } else {
        $('#new-link-button').show();
        $('#max-links').hide();
    }

  });

  //form toggle
  $("#new-link-button").click(function(e) {
    e.preventDefault();    
    $("#add-link-form").slideToggle("100");
  });

});
