// page-wide helper definitions
.max-width { max-width: 1080px; margin: 0 auto; } // limit width of some rows
.lt-designer-root .lt-responsive-row.max-width { margin: 1em auto; } // ... and center them in design view
.starlings-background, .lt-designer-root .lt-responsive-row.starlings-background { background-size: cover; background-position: center center; background-image: url('https://lachlandev.sharepoint.com/sites/macnetdev/techassist/PublishingImages/starlings.jpg');}
.nomargin.row { margin-right: 0; margin-left: 0;}
.filter-tag{
    border: 1px solid white;
    padding: 5px;
    color: white;
    font-size: 18px;
}

.card {
     //  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.05), 0 3px 3px rgba(0, 0, 0, 0.10);
}
// component styles
img.techassist-logo {
    border-right: 3px solid #ffd600;
    height: 60px;
    width: auto;
    margin-right: 24px;
    transition: all 0.4s;
    position: absolute;
    top: 0;
    left: 0;
}
.navbar{
    display:none;
}
div.search-field-container {

    min-height: 35px;

    right: 0;
    top: 0;
    transition: left 0.4s;
    width: 100%;
}
.bootstrap-tagsinput input{
    height:100%;
}
.label-info {
    background-color: #696969;
    border-radius: 2px;
    font-size: 15px;
    font-weight: 400;
}
  .list-group-item:first-child, .list-group-item:last-child{
    border-radius: 0px;
  }

.bootstrap-tagsinput, .search-field {
    height: initial;
    min-height: 35px;
    padding: 20px;
    font-size: 18px;
    font-family: "helvetica neue", arial, helvetica, sans-serif;
    border: 0;
    box-shadow: 0 0 3px rgba(0,0,0,0.2);
    width: 100%;
    opacity: 0.9;
    transition: all 0.4s;
    background-color: white;
    color: black;
    border-left: none;
border-radius:0px!important;
line-height:30px!important;

}

.ta-search-button {
    position: absolute;
    top: 0;
    right: 0;
    min-height: 35px;
    width: 60px;
    line-height: 35px;
    border: 0;
    background-color: none;
    background-image: none;
    color: #333;
    text-align: center;
    color: #333 !important;

    &:hover, &:active {
        text-decoration: none;
    }

    &:after {
        content: "\f002";
        font-family: fontawesome;
        font-size: 20px;
    }
}

.ta-search-section { 
min-height: 35px; 
width: 100%; 
position: relative; 
}

.ta-search-section.active {
    img.techassist-logo { left: -261px; }
    .search-field-container { left: 3px; }
    input.search-field {opacity: 1.0;}
}


@media screen and (max-width: 600px) {
    .ta-search-section  {
        input.search-field { opacity: 0.0; }
        .ta-search-button { color: white; }

        &.active {
            input.search-field { opacity: 1.0; }
            .ta-search-button {color: black;}
        }
    }
    .techassist-logo{
        display: none;
    }
    div.search-field-container {

    left: 0px;
    }
}

