require(['//cdnjs.cloudflare.com/ajax/libs/bootstrap-tagsinput/0.8.0/bootstrap-tagsinput.js'], function () {
    var resultsTartget = $('#results');
    var peopleResultsTartget = $('#peopleResults');
    var resourceResultsTartget = $('#resourceResults');

    $('#graphSearchButton').click(function () {
        LTGraph.Load($('#graphSearchInput').val(), resultsTartget);
        LTGraph.Load($('#graphSearchInput').val(), peopleResultsTartget);
        LTGraph.Load($('#graphSearchInput').val(), resourceResultsTartget);
    });
    $(".bootstrap-tagsinput input").keyup(function () {
      //  LTGraph.Load($('#graphSearchInput').val(), resultsTartget);
       // LTGraph.Load($('#graphSearchInput').val(), peopleResultsTartget);
       // LTGraph.Load($('#graphSearchInput').val(), resourceResultsTartget);
    });
    $('#graphSearchInput').on('itemAdded', function (event) {
        LTGraph.Load($('#graphSearchInput').val(), resultsTartget);
        LTGraph.Load($('#graphSearchInput').val(), peopleResultsTartget);
        LTGraph.Load($('#graphSearchInput').val(), resourceResultsTartget);
    });
    $('#graphSearchInput').on('itemRemoved', function (event) {
        debugger;
        if ($('#graphSearchInput').val()) {
            LTGraph.Load($('#graphSearchInput').val(), resultsTartget);
            LTGraph.Load($('#graphSearchInput').val(), peopleResultsTartget);
            LTGraph.Load($('#graphSearchInput').val(), resourceResultsTartget);

        } else {
            LTGraph.Load('*', resultsTartget);
            LTGraph.Load('*', peopleResultsTartget);
            LTGraph.Load('*', resourceResultsTartget);

        }
    });


    $('.filter').click(function () {
        debugger;
        var classes = $(this).attr('class').split(' ');
        classes.forEach(function (el) {
            if (el.startsWith("filter") && el != "filter") {
                el = el.replace(/filter-/g, '');
                $('.search-filter-container').tagsinput('removeAll');
                $('.search-filter-container').tagsinput('add', el.replace(/-/g, ' '));
            }
        });
    }
    );

});
