children.css:
@darkBlue: rgb(41,39,76);

.tabsContainer {
    padding: 20px 20px 0px 20px;

    .tab {
        display: inline-block;
        margin: 0 0.35%;
        width: 24%;
        text-align: center;
        border-left: solid 3px @darkBlue;
        border-right: solid 3px @darkBlue;
        border-top: solid 3px @darkBlue;
        border-bottom: solid 3px @darkBlue;
        background: white;
        padding: 20px 0px;
        cursor: pointer;
    }

    .tab.active {
        border-bottom: solid 3px white;
    }

    .title {
        font-size: 18px;
        font-weight: bold;
        color: @darkBlue;
    }


}

.roadmapImage {
    margin-left: 0px;
    margin-right: 0px;
    margin-top: 0px;
    margin-bottom: 0px;
    height: 500px;
    background-position-y: top;

    a {
        display: block;
        height: 100%;
        width: 100%;
    }
}

.tabContentContainer {
    border-top: solid 3px @darkBlue;
    margin-top: -3px;
    background-color: white;
    display: none;



    .content {
        position: relative;
        width: ~"calc(100% - 10px)";
        display: inline-block;
        overflow: hidden;
        height: 680px;
        margin-left:5px;
    }

    .options {
        display: inline-block;
        vertical-align: top;

        .option {
            height: 30px;
            width: 30px;
            background-size: contain;
            background-position: center;
            background-repeat: no-repeat;
            margin: 10px 5px;

            a {
                display: block;
                height: 100%;
                width: 100%;
            }
        }



    }

}

.tabContentContainer.active {
    display: block;

     border-left: solid 3px @darkBlue;
        border-right: solid 3px @darkBlue;
        border-bottom: solid 3px @darkBlue;
}

.milestonesContainer {
    .content {
        position: relative;
        width: ~"calc(100% - 50px)";
        display: inline-block;
        overflow: hidden;
        height: 700px;
        margin-left:5px;
    }
}
.howToAndToolsContainer {
    .content {
        position: relative;
        width: ~"calc(100% - 50px)";
        display: inline-block;
        overflow: hidden;
        height: 700px;
        margin-left:5px;
    }
}
